styles:
:host {
    display: grid;
    grid-template-areas: "label   field" ". feedback";
    grid-template-columns: 3fr 7fr;
    grid-template-rows: 1fr auto;
    margin-bottom: 1rem;
    height: auto;
    visibility: visible;
    z-index: 50;
}

:host-context(.hide-label) {
    grid-template-areas: "field" "feedback";
    grid-template-columns: 1fr;
}

:host-context(.hide-feedback) {
    grid-template-areas: "label   field";
    grid-template-columns: 3fr 7fr;
}

:host-context(.hide-label.hide-feedback) {
    grid-template-areas: "field";
}

:host-context(.hidden) {
    display: none !important;
}

form-label {
    grid-area: label;
}

form-field {
    grid-area: field;
}

form-feedback {
    grid-area: feedback;
}

:host {
    display: flex;
    align-items: center;
    color: rgba(0, 0, 0, 0.85);
    font-size: 1.6rem;
    font-weight: 700;
    position: relative
}

:host {
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    width: 100%;
    color: #f5222d;
    font-size: 1.2rem;
    margin-top: 0.5rem;
}

:host {
    background-color: white;
    border-radius: 5px;
    box-sizing: border-box;
    border: 1px solid rgb(196, 202, 212);
    overflow-y: scroll;
    padding: 0 10px;
    position: absolute;
    top: 100%;
    z-index: 9999;
    width: 100%;
    max-height: 20rem;
}

:host {
    display: flex;
    flex: 1;
}

textarea {
    border: none;
    font-size: 1.5rem;
    padding: 1rem;
    width: 100%;
    max-width: 100%;
    overflow-y: scroll;
}
